import React, { Component } from 'react';

import PropTypes from 'prop-types';

import { withStyles } from '@material-ui/core/styles';

import Fab from '@material-ui/core/Fab';

import EmptyState from '../layout/EmptyState/EmptyState';
import Button from '@material-ui/core/Button';
 
// import AddTrunkDialog from '../../dialogs/AddTrunkDialog/AddTrunkDialog';
// import TrunksView from '../../layout/TrunksView';
// import TrunkCard from '../../layout/TrunkCard';


import ReactDataGrid from 'react-data-grid';


const styles = (theme) => ({
  emptyStateIcon: {
    fontSize: theme.spacing(12)
  },

  button: {
    marginTop: theme.spacing(1)
  },

  buttonIcon: {
    marginRight: theme.spacing(1)
  },

  buttonTop: {
    marginTop: 0
  },
});



class TrunkContent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      items: []
    };
  }



  addItem = (event) => {  
    var postData = this.props.trunk;
    if (postData.items == undefined) {
      postData.items = []
    }
    postData.items.push({name: "item name", quanitity: 10});
    
    var updates = {};
    updates['trunks/' + postData.key] = postData;

    global.firebaseApp.database().ref().update(updates, 
        function(error) {
            if (error) {
                console.log(error)
            } else {
                console.log("Success")
            }
        }
    );
  }


//   updateTrunkStatus = (event) => {  
//     var postData = this.props.trunk;
//     postData.status = event.target.value;
    
//     var updates = {};
//     updates['trunks/' + postData.key] = postData;

//     global.firebaseApp.database().ref().update(updates, 
//         function(error) {
//             if (error) {
//                 console.log(error)
//             } else {
//                 console.log("Success")
//             }
//         }
//     );
// }

  render() {
    // Styling
    const { classes } = this.props;

    // Properties
    const { isSignedIn, title, trunk} = this.props;
    
    
    const columns = [
    // { key: 'id', name: 'ID' },
    { key: 'item', name: 'Item' },
    { key: 'quantity', name: 'Quantity' } 
    ];
    
  
    var rows = [{id: "43", item: "rthge", quantity: "554"}];

    // var rows = function () {
    //   var rowsArr = []

    //   this.props.trunk.items.forEach(item => {
    //     rowsArr.push({id: item.name, item: item.name, quantity: item.quantity})
    //   });
    //   return rowsArr
    // }

    console.log("ROWS")

    console.log(rows)


  
    if (isSignedIn) {
          // return (<Button onClick={this.addItem}>add</Button>);
        return (<ReactDataGrid
            columns={columns}
            rowGetter={i => rows[i]}
            rowsCount={3}
            minHeight={150} />
        );
    } else {
        return (
        <EmptyState
            title={title}
            description="Sign up or sign in to edit this trunk"
        />
        );
    }
  }
}

TrunkContent.propTypes = {
  classes: PropTypes.object.isRequired,
  isSignedIn: PropTypes.bool.isRequired,
  title: PropTypes.string,
  trunk: PropTypes.object.isRequired
};

export default withStyles(styles)(TrunkContent);
